import '../styles/App.scss';
import phrases from'../data/phrases.json';
import { useState } from 'react';



function App() {

  const [data, setData] = useState(phrases)
  const [search, setSearch] = useState("");
  const [allCharacters, setAllCharacters] = useState([])
  

//con una variable
  const htmlData = data
 
  .map((eachPhrase) => (
     <li className='list'> 
     <p> {eachPhrase.quote} </p>
      <span>{eachPhrase.character}</span>
     </li>
  ));

//funcion handleclick

//funcion filtro
const hadleFilter = (ev => {

  setSearch(ev.target.value);
});

 const renderCharacterOptions = () => {
    return allCharacters.map((character, index) => {
    return <option key={index} value={character.toLowerCase()}>{character}</option>})
  }

  return (
    <div className="page">
      {/* header filtro */}
      <header className="header">
          <h1 className="header__title">Frases de Friends</h1>
        <form className='form'>

          <label htmlFor="quote-filter">Filtrar por frases</label>
          <input
            className="form__search"
            type="text"
            name="search"
            id='search'
            onChange={hadleFilter}
            value={search}

          />

          <label htmlFor="caracter-filter">Filtrar por personaje</label>
           <select
            className="form__search"
            name="character"
            id='search-character'
            onChange={hadleFilter}
            >
              <option value="">Todos</option>
              {renderCharacterOptions()}
       </select>
        </form>
      </header>

      <main>
        {/* contact list */}
        <ul className="main__quotes">
        {htmlData}
         
        </ul>

        {/* new Phrase */}
        <form className="new-contact__form">
          <h2 className="new-contact__title">Añadir una nueva frase</h2>
          <label htmlFor="">Frase:</label>
          <input
            className="new-contact__input"
            type="text"
            name="quote"
            id="quote"
            placeholder='Ej. Unaji!'

          />
          <label htmlFor="">Paersonaje:</label>
          <input
            className="new-contact__input"
            type='text'
            name="character"
            id="character"
            placeholder='EJ. Ross'
      
          />
          <input className="new-contact__btn" type="submit" value="Añadir nueva frase"/>
        </form>
      </main>
    </div>
  );
}

export default App;
